	plot3(n4line(:,1),n4line(:,2),n4line(:,3),'b-')
	hold off
	
elseif data.CrossSec == 2 || data.CrossSec == 5
	for i=1:length(xspan)
		cline(i,:) = S(xspan(i),0,0)*ee;
		yline(i,:) = S(xspan(i),W/2,0)*ee;
		ynline(i,:) = S(xspan(i),-W/2,0)*ee;
		zline(i,:) = S(xspan(i),0,W/2)*ee;
		znline(i,:) = S(xspan(i),0,-W/2)*ee;
	end

	aspan = 0:0.1:2*pi;
	for i=1:length(aspan)
		c1line(i,:) = S(-L/2,W/2*cos(aspan(i)),W/2*sin(aspan(i)))*ee;
		c2line(i,:) = S(-L/6,W/2*cos(aspan(i)),W/2*sin(aspan(i)))*ee;
		c3line(i,:) = S(L/6,W/2*cos(aspan(i)),W/2*sin(aspan(i)))*ee;
		c4line(i,:) = S(L/2,W/2*cos(aspan(i)),W/2*sin(aspan(i)))*ee;
	end

	plot3(cline(:,1),cline(:,2),cline(:,3),'k.-')
	hold on
	plot3(yline(:,1),yline(:,2),yline(:,3),'r-')
	plot3(zline(:,1),zline(:,2),zline(:,3),'g-')
	plot3(ynline(:,1),ynline(:,2),ynline(:,3),'r-')
	plot3(znline(:,1),znline(:,2),znline(:,3),'g-')
	plot3(c1line(:,1),c1line(:,2),c1line(:,3),'b-')
	plot3(c2line(:,1),c2line(:,2),c2line(:,3),'b-')
	plot3(c3line(:,1),c3line(:,2),c3line(:,3),'b-')
	plot3(c4line(:,1),c4line(:,2),c4line(:,3),'b-')
	hold off
	
else
	error('Cross section not implemented! Error in draw34X3.m')
end

